mapper.select:

select id, accountId, puuid, profileIconId, revisionDate, name AS "summonerName", summonerLevel
from tbl_summoner
where name = #{summonerName}

select match_id, data_version
from tbl_metadata
where match_id = #{match_id}

select participants
from tbl_match_participant
where match_id = #{match_id}
